INSERT INTO boards ( user_id, participation_type, title, topics, content, link, is_verified, ai_verified, start_date, end_date, region, district ) VALUES 
(1, '집회', '927 기후정의행진', '환경', '올 여름도 역대급 폭염과 폭우를 겪었습니다. 기후재난 속에 일터와 삶터에서 사람들이 쓰러지고, 생명이 희생 당하고 있습니다. 새로운 민주주의 / 기후정의 실현 / 부정의하고 불평등한 세상을 바꾸는 기후정의행진에 많은 관심 부탁드립니다!', 'https://action4climatejustice.kr/', false, 1, '2025-09-27 15:00:00', '2025-09-27 17:30:00', '서울특별시', '종로구'), 
(1, '행사', '일본군성노예제 문제해결을 위한 35년의 연구 성과와 대항 기억의 미래 심포지엄', '추모/기억,인권', '정의기억연대와 일본의 연구자 단체 Fight For Justice는 이번 공동 심포지엄을 통해 지난 연구와 활동의 성과를 짚어보고, 향후 나아갈 방향을 함께 모색하고자 합니다. 많은 관심과 참여 부탁드립니다.', 'https://womenandwarmuseum.net/announcement/?bmode=view&idx=168908183', false, 1, '2025-12-18 14:00:00', '2025-12-18 18:30:00', '서울특별시', '종로구'), 
(1, '집회', '12.3 내란외환 청산과 종식, 사회대개혁 시민대행진', '인권,추모/기억', '아직 내란은 끝나지 않았습니다. 12.3 내란을 막아냈던 국회에 다시 모여 내란정당 국민의힘을 규탄하고 내란청산 사회대개혁을 바라는 국민의 목소리가 필요합니다! 12.3 계엄 1년, 국회 앞으로 모여주세요!', 'https://x.com/hanitweet/status/1995787524459868437', false, 1, '2025-12-03 19:00:00', '2025-12-03 21:00:00', '서울특별시', '영등포구'), 
(1, '행사', '안산기억문화제', '추모/기억,인권', '세월호참사 10년을 지나 다시 맞는 1년. 기억의 힘과 응원봉의 빛으로 만들어 갈 새로운 세계를 꿈꾸며 다양한 시민의 목소리와 행동을 엮어 열한 번째 봄을 활짝 열고자 합니다.', 'https://416foundation.org/news/%EA%B3%B5%EC%A7%80%EC%82%AC%ED%95%AD/?mod=document&uid=2874', false, 1, '2025-04-12 14:00:00', '2025-04-12 19:30:00', '경기도', '안산시'), 
(1, '집회', '제39회 한국여성대회 대행진', '여성', '3월 8일 여성의 날은 오직 여성을 위하는 날로, 서로의 안위를 확인하고 연대의 힘을 느낄 수 있는 날입니다. 더불어 4.10 총선을 앞둔 지금, 정치를 바꿀 우리의 힘을 선명하게 가시화 할 수있는 날이 될 것입니다. 우리가 흩어지지 않았음을 다시 한 번 세상에 선포합시다!', 'https://women21.or.kr/notice/23673', false, 1, '2024-03-08 17:00:00', '2024-03-08 19:00:00', '서울특별시', '중구'), 
(1, '행사', '차별금지법 제정을 위한 손솔의 작전회의 : 광장의 친구들을 만나다', '인권', '광장 자원봉사자 및 시민들을 국회로 초대합니다! 차별금지법 제정을 위한 전략부터, 일상의 액션까지. 이제 국회 앞 광장의 외침을 넘어 우리 삶의 현장을 바꾸기 위한 구체적인 작전회의. 국회의원 손솔과의 작전회의에 단순한 참관이 아닌, 세상을 바꿀 미션을 설계하는 동료로 함께해주세요!', 'https://docs.google.com/forms/d/e/1FAIpQLScjyv36SiDiX7_mleoIuEjIde4w331Z_s4To1Z6UqAUa_IIiQ/viewform', false, 1, '2025-01-17 10:00:00', '2025-01-17 17:00:00', '서울특별시', '영등포구'),
(1, '행사', '[토론회] 낳지 않을 결심 : 젠더 불평등과 저출생- 정부의 저출생 대응 담론과 정책 진단', '여성,인권', '이번에 발표된 정부의 저출생 정책은 ‘인구비상사태’를 선언하며 범국가적 위기라는 점을 강조한 것에 비해, 저출생 문제의 핵심적인 요인으로 꼽히는 ‘젠더 불평등’과 ‘성차별’이라는 구조적 문제를 외면하고 있으며, 저출생 현실이 지닌 다면성을 도외시하고 있습니다. 이에 성평등 관점 없는 정부의 저출생 대응 담론과 정책의 문제를 진단하고 대안을 모색하는 토론회를 개최합니다.', 'https://women21.or.kr/index.php?&mid=notice&listStyle=list&page=18&document_srl=24639', false, 1, '2024-07-23 15:00:00', '2024-07-23 20:00:00', '서울특별시', '종로구'),
(1, '행사', '10.29 이태원 참사 3주기 기억식', '추모/기억', '10.29 이태원 참사 3주기 그 날의 진실과 정의를 바로세우는 그 길에 함께 해 주세요. 10/25(토) 18:34 3주기 시민추모대회 (서울광장, 자율참가) 10/29(수) 10:29 3주기 기억식 (광화문북광장, 사전신청/추첨)', 'https://docs.google.com/forms/d/e/1FAIpQLSc1YYJoK-hBJhwvx4X9IsYbQDs-W31do3pFezjqbVvUnC2JwA/viewform', false, 1, '2025-10-29 18:34:00', '2025-10-29 19:35:00', '서울특별시', '중구'),
(1, '행사', '10.29 이태원 참사 진실대행진', '추모/기억', '4월 10일 22대 국회의원 총선거를 앞두고 유가족들은 4월 4일부터 7일까지 부산, 광주, 전주, 대전, 수원, 서울 등 전국을 도는 "진실에 투표하세요" 진실대행진을 떠납니다. 진실대행진 출발 기자회견을 4월 4일 오전 9시 30분,분향소 앞에서 개최합니다.', 'https://docs.google.com/document/d/1G52Zp4ceYdFmQD4bRRoFh9QomLmFTonu7xihWdBurkY/mobilebasic', false, 1, '2024-04-04 09:30:00', '2024-04-09 18:00:00', '서울특별시', '용산구'),
(1, '집회', '미아역 여성혐오 살인사건 규탄 시위 : 여자라서 죽었다. 여성 테러 방치하는 경찰도 공범이다', '여성', '미아역 여성 살해 사건, 여성혐오 범죄로 인정하라! 강남역부터 미아역까지 이어진 여성 테러, 우리가 끝내자 오는 5월 17일, 강남역 여성혐오 살인사건 9주기를 추모하며 미아역 여성 테러 사건을 여성혐오범죄로 인정받기 위한 싸움을 시작한다.', 'https://x.com/endfemicide2023/status/1917941453168513343', false, 1, '2025-05-17 15:00:00', '2025-05-17 17:00:00', '서울특별시', '강북구'),
(1, '집회', '공공복지 확대 촉구 시민 집회', '복지,인권', '공공복지 확대와 사회안전망 강화를 요구하는 시민 집회입니다. 복지는 시혜가 아니라 권리이며, 누구나 최소한의 삶을 보장받아야 합니다.', 'https://example.com/rally/2023-01', false, 1, '2023-06-17 14:00:00', '2023-06-17 17:00:00', '서울특별시', '중구'),
(1, '집회', '장애인 복지 예산 삭감 반대 집회', '복지,장애인', '장애인 복지 예산 삭감에 반대하며 이동권과 돌봄권 보장을 요구하는 집회입니다. 복지 후퇴는 곧 삶의 권리 후퇴입니다.', 'https://example.com/rally/2023-02', false, 1, '2023-10-21 13:00:00', '2023-10-21 16:00:00', '대전광역시', '서구'),
(1, '집회', '노동자 생존권과 복지 강화를 위한 집회', '복지,노동자', '불안정 노동과 복지 사각지대를 해소하기 위해 노동자들이 함께 모이는 집회입니다. 안정적인 삶을 위한 복지 강화가 필요합니다.', 'https://example.com/rally/2024-01', false, 1, '2024-03-09 15:00:00', '2024-03-09 18:00:00', '부산광역시', '부산진구'),
(1, '집회', '청소년 복지 확대 촉구 집회', '복지,청소년', '청소년을 권리의 주체로 인정하고 복지 정책 확대를 요구하는 집회입니다. 청소년의 삶은 미래가 아니라 현재입니다.', 'https://example.com/rally/2025-01', false, 1, '2025-05-24 14:00:00', '2025-05-24 16:00:00', '광주광역시', '북구'),
(1, '집회', '여성 빈곤 문제 해결을 위한 복지 집회', '복지,여성', '여성 빈곤과 돌봄 부담을 해결하기 위한 복지 정책 강화를 요구하는 집회입니다. 성평등한 복지가 필요합니다.', 'https://example.com/rally/2026-01', false, 1, '2026-02-07 14:00:00', '2026-02-07 17:00:00', '경상남도', '창원시'),
(1, '행사', '복지 사각지대 해소 정책 토론회', '복지,인권', '복지 사각지대에 놓인 시민들의 현실을 공유하고 정책 대안을 논의하는 토론회입니다. 누구도 배제되지 않는 복지를 이야기합니다.', 'https://example.com/event/2023-01', false, 1, '2023-04-12 10:00:00', '2023-04-12 13:00:00', '전라북도', '전주시'),
(1, '행사', '장애인 복지 제도 개선 간담회', '복지,장애인', '장애인의 일상과 직결된 복지 제도의 문제점을 점검하고 개선 방향을 모색하는 간담회입니다. 현장의 목소리를 중심으로 논의합니다.', 'https://example.com/event/2023-02', false, 1, '2023-12-02 14:00:00', '2023-12-02 16:30:00', '서울특별시', '강남구'),
(1, '행사', '노동자 복지 정책 설명회', '복지,노동자', '노동자를 위한 복지 제도와 지원 정책을 소개하고 현장의 의견을 나누는 설명회입니다. 누구나 이해할 수 있게 안내합니다.', 'https://example.com/event/2024-01', false, 1, '2024-09-05 19:00:00', '2024-09-05 21:00:00', '경기도', '성남시'),
(1, '행사', '청소년 복지 정책 포럼', '복지,청소년', '청소년 복지 정책의 현황과 한계를 짚고 새로운 대안을 논의하는 포럼입니다. 청소년의 권리를 중심에 두고 이야기합니다.', 'https://example.com/event/2025-01', false, 1, '2025-02-15 13:00:00', '2025-02-15 16:00:00', '충청북도', '청주시'),
(1, '행사', '여성 복지와 돌봄 정책 세미나', '복지,여성', '여성의 삶과 돌봄 현실을 중심으로 복지 정책을 재구성하기 위한 세미나입니다. 지속 가능한 돌봄 체계를 논의합니다.', 'https://example.com/event/2026-01', false, 1, '2026-01-17 14:00:00', '2026-01-17 17:00:00', '제주특별자치도', '제주시'),
(1, '집회', '동물학대 처벌 강화를 위한 전국 집회', '동물권,범죄/사법', '반복되는 동물학대 사건에 대한 강력한 처벌과 제도 개선을 요구하는 시민 집회입니다. 동물도 고통을 느끼는 생명이며 보호받아야 할 권리가 있습니다.', 'https://example.com/rally/animal1', false, 1, '2023-09-16 14:00:00', '2023-09-16 17:00:00', '서울특별시', '강서구'),
(1, '집회', '퀴어 시민의 안전과 존엄을 위한 거리 집회', '성소수자,인권', '성소수자에 대한 차별과 혐오를 중단하고 안전하게 살아갈 권리를 요구하는 집회입니다. 우리는 숨지 않고 시민으로 살 권리가 있습니다.', 'https://example.com/rally/lgbt1', false, 1, '2024-06-22 15:00:00', '2024-06-22 18:00:00', '서울특별시', '송파구'),
(1, '집회', '동물권과 공존 사회를 위한 시민 집회', '동물권,인권', '동물을 소유물이 아닌 권리의 주체로 인정하는 사회를 요구하는 집회입니다. 인간 중심의 개발과 소비 구조를 되돌아봐야 합니다.', 'https://example.com/rally/animal2', false, 1, '2025-10-12 14:00:00', '2025-10-12 17:00:00', '부산광역시', '해운대구'),
(1, '행사', '성소수자 인권과 제도 개선 정책 토론회', '성소수자,인권', '성소수자의 일상에서 마주하는 차별과 제도적 공백을 점검하고 개선 방안을 논의하는 정책 토론회입니다.', 'https://example.com/event/lgbt1', false, 1, '2024-11-09 13:00:00', '2024-11-09 16:00:00', '대전광역시', '서구'),
(1, '행사', '동물권 활동가 네트워크 포럼', '동물권,복지', '동물 보호 현장의 경험을 공유하고 제도 개선을 논의하는 활동가 포럼입니다. 동물 복지는 사회의 성숙도를 보여줍니다.', 'https://example.com/event/animal1', false, 1, '2026-01-24 11:00:00', '2026-01-24 14:00:00', '경기도', '고양시'),
(1, '집회', '여성폭력 근절 촉구 집회', '여성', '여성폭력 근절과 피해자 보호 강화를 요구하는 집회입니다. 안전한 사회는 여성의 권리 보장에서 시작되며, 실효성 있는 지원 체계가 필요합니다.', 'https://example.com/rally/w-2023-0308-busan', false, 1, '2023-03-08 14:00:00', '2023-03-08 17:00:00', '부산광역시', '서구'),
(1, '집회', '청소년 인권 보장 촉구 집회', '청소년', '청소년을 보호의 대상이 아닌 권리의 주체로 인정하고 인권 보장을 요구하는 집회입니다. 참여와 표현의 권리를 현실에서 보장해야 합니다.', 'https://example.com/rally/y-2023-0505-gwangju', false, 1, '2023-05-05 15:00:00', '2023-05-05 17:00:00', '광주광역시', '북구'),
(1, '집회', '노동시간 단축 요구 노동자 집회', '노동자', '과도한 노동시간을 줄이고 노동자의 건강권과 삶의 질을 높이기 위한 집회입니다. 안전한 일터와 휴식 가능한 삶이 필요합니다.', 'https://example.com/rally/l-2023-0902-ulsan', false, 1, '2023-09-02 14:00:00', '2023-09-02 18:00:00', '울산광역시', '남구'),
(1, '집회', '퀴어 혐오 반대 시민 집회', '성소수자', '성소수자에 대한 혐오와 차별을 반대하고 평등한 권리를 요구하는 집회입니다. 누구도 정체성 때문에 위축되지 않는 사회를 만듭니다.', 'https://example.com/rally/q-2024-0615-daegu', false, 1, '2024-06-15 16:00:00', '2024-06-15 19:00:00', '대구광역시', '중구'),
(1, '집회', '농민 생존권 보장 집회', '농민', '농산물 가격 보장과 농민 생존권 보호를 요구하는 집회입니다. 지속 가능한 농업을 위해 생산자 권리와 지역 공동체를 지켜야 합니다.', 'https://example.com/rally/f-2024-1001-jeonbuk-gimje', false, 1, '2024-10-01 13:00:00', '2024-10-01 16:00:00', '전라북도', '김제시'),
(1, '집회', '장애인 이동권 보장 집회', '장애인', '장애인의 이동권과 접근성 보장을 촉구하는 집회입니다. 이동은 기본권이며, 시설·교통·정보 접근 개선이 동반되어야 합니다.', 'https://example.com/rally/d-2023-1203-incheon', false, 1, '2023-12-03 14:00:00', '2023-12-03 17:00:00', '인천광역시', '부평구'),
(1, '집회', '교육 공공성 강화 집회', '교육', '교육의 공공성을 지키고 학생 중심 교육을 요구하는 집회입니다. 교육은 경쟁만이 아니라 성장과 돌봄을 포함해야 합니다.', 'https://example.com/rally/e-2024-0320-anyang', false, 1, '2024-03-20 15:00:00', '2024-03-20 18:00:00', '경기도', '안양시'),
(1, '집회', '기후위기 대응 촉구 집회', '환경', '기후위기에 대응하기 위한 실질적인 정책 마련을 요구하는 집회입니다. 지역의 전환 계획과 시민 참여가 함께 보장되어야 합니다.', 'https://example.com/rally/env-2024-0922-jeju', false, 1, '2024-09-22 14:00:00', '2024-09-22 17:00:00', '제주특별자치도', '제주시'),
(1, '집회', '공공의료 확대 요구 집회', '의료', '공공의료 인프라 확충과 의료 접근성 강화를 요구하는 집회입니다. 지역 의료 공백을 줄이고 필수의료를 지켜야 합니다.', 'https://example.com/rally/m-2025-0201-chungbuk-cheongju', false, 1, '2025-02-01 13:00:00', '2025-02-01 16:00:00', '충청북도', '청주시'),
(1, '집회', '차별 없는 사회를 위한 인권 집회', '인권', '모든 차별에 반대하고 평등한 인권 보장을 요구하는 시민 집회입니다. 일상에서 배제와 혐오를 멈추는 기준이 필요합니다.', 'https://example.com/rally/hr-2025-0501-gyeongnam-jinju', false, 1, '2025-05-01 14:00:00', '2025-05-01 17:00:00', '경상남도', '진주시'),
(1, '집회', '동물학대 중단 촉구 집회', '동물권', '동물학대 중단과 생명 존중 사회를 요구하는 집회입니다. 학대 방지와 책임 있는 보호 체계를 강화해야 합니다.', 'https://example.com/rally/ar-2023-1015-gyeonggi-paju', false, 1, '2023-10-15 13:00:00', '2023-10-15 16:00:00', '경기도', '파주시'),
(1, '집회', '복지 예산 확대 촉구 집회', '복지', '복지 예산 확대와 사회안전망 강화를 요구하는 시민 집회입니다. 돌봄과 주거, 생계 지원의 공백을 줄이기 위한 정책이 필요합니다.', 'https://example.com/rally/wf-2024-1110-gangwon-chuncheon', false, 1, '2024-11-10 14:00:00', '2024-11-10 17:00:00', '강원특별자치도', '춘천시'),
(1, '집회', '10.29 참사 희생자 추모 집회', '추모/기억', '10.29 참사 희생자를 기억하고 애도하는 추모 집회입니다. 잊지 않기 위한 약속과 공동체의 책임을 함께 이야기합니다.', 'https://example.com/rally/mem-2024-1029-busan-junggu', false, 1, '2024-10-29 18:00:00', '2024-10-29 20:00:00', '부산광역시', '중구'),
(1, '집회', '정의로운 사법을 촉구하는 시민 집회', '범죄/사법', '공정한 수사와 정의로운 사법 판단을 촉구하는 시민 집회입니다. 피해자의 목소리가 제대로 반영되는 절차가 필요합니다.', 'https://example.com/rally/j-2025-0615-jeonnam-목포', false, 1, '2025-06-15 14:00:00', '2025-06-15 17:00:00', '전라남도', '목포시'),
(1, '집회', '청소년 노동 착취 반대 집회', '청소년', '청소년 노동 착취를 중단하고 안전한 노동 환경을 요구하는 집회입니다. 기본적인 보호장치와 신고·지원 체계를 강화해야 합니다.', 'https://example.com/rally/y-2026-0214-gyeongbuk-gumi', false, 1, '2026-02-14 15:00:00', '2026-02-14 18:00:00', '경상북도', '구미시'),
(1, '행사', '여성 권리 증진 정책 토론회', '여성', '여성의 권리 증진을 위한 정책 방향을 논의하는 시민 토론회입니다. 안전과 돌봄, 노동 환경을 포함한 현실적 대안을 모색합니다.', 'https://example.com/event/w-2023-1118-daejeon-yuseong', false, 1, '2023-11-18 13:00:00', '2023-11-18 16:00:00', '대전광역시', '유성구'),
(1, '행사', '청소년 참여 민주주의 워크숍', '청소년', '청소년의 정치·사회 참여 확대를 위한 워크숍입니다. 지역에서 가능한 참여 방식과 의사표현 채널을 함께 설계합니다.', 'https://example.com/event/y-2024-0224-gyeonggi-seongnam', false, 1, '2024-02-24 10:00:00', '2024-02-24 13:00:00', '경기도', '성남시'),
(1, '행사', '노동 안전 교육 세미나', '노동자', '산업 현장의 안전을 강화하기 위한 교육 중심 세미나입니다. 사고 예방과 권리 안내, 실제 사례를 바탕으로 실천 방법을 공유합니다.', 'https://example.com/event/l-2024-0706-chungnam-cheonan', false, 1, '2024-07-06 14:00:00', '2024-07-06 17:00:00', '충청남도', '천안시'),
(1, '행사', '성소수자 인권 문화제', '성소수자', '성소수자 인권을 알리고 연대를 나누는 문화 행사입니다. 안전한 공간에서 경험과 이야기를 공유하며 서로를 지지합니다.', 'https://example.com/event/q-2024-0928-gwangju-donggu', false, 1, '2024-09-28 16:00:00', '2024-09-28 20:00:00', '광주광역시', '동구'),
(1, '행사', '농민 정책 간담회', '농민', '농업 정책 개선과 농민의 현실을 논의하는 간담회입니다. 현장 경험을 토대로 필요한 지원과 제도 개선을 정리합니다.', 'https://example.com/event/f-2025-0117-jeonnam-naju', false, 1, '2025-01-17 14:00:00', '2025-01-17 16:00:00', '전라남도', '나주시'),
(1, '행사', '장애인 권리 보장 포럼', '장애인', '장애인의 권리 보장과 제도 개선을 논의하는 포럼입니다. 이동·정보·고용 등 생활 전반의 장벽을 점검합니다.', 'https://example.com/event/d-2025-0329-busan-sasang', false, 1, '2025-03-29 13:00:00', '2025-03-29 16:00:00', '부산광역시', '사상구'),
(1, '행사', '교육 격차 해소 정책 세미나', '교육', '지역 간 교육 격차 해소를 위한 정책 방향을 논의하는 세미나입니다. 지원 체계와 현장 적용 방안을 구체적으로 다룹니다.', 'https://example.com/event/e-2025-0621-gyeonggi-pyeongtaek', false, 1, '2025-06-21 14:00:00', '2025-06-21 17:00:00', '경기도', '평택시'),
(1, '행사', '환경 보호 시민 캠페인', '환경', '생활 속 환경 보호 실천을 알리는 시민 참여 캠페인입니다. 지역에서 가능한 실천과 정책 요구를 함께 정리합니다.', 'https://example.com/event/env-2025-0907-gangwon-sokcho', false, 1, '2025-09-07 13:00:00', '2025-09-07 16:00:00', '강원특별자치도', '속초시'),
(1, '행사', '공공의료 정책 설명회', '의료', '공공의료 정책과 지역 의료 강화 방안을 소개하는 설명회입니다. 필수의료 공백을 줄이기 위한 실행 과제를 공유합니다.', 'https://example.com/event/m-2026-0110-gyeongnam-tongyeong', false, 1, '2026-01-10 14:00:00', '2026-01-10 16:00:00', '경상남도', '통영시'),
(1, '행사', '인권 감수성 향상 시민 강연', '인권', '일상에서의 인권 감수성을 높이기 위한 시민 강연입니다. 차별과 혐오를 줄이기 위한 실천 기준을 함께 나눕니다.', 'https://example.com/event/hr-2026-0221-jeonbuk-iksan', false, 1, '2026-02-21 15:00:00', '2026-02-21 17:00:00', '전라북도', '익산시'),
(1, '서명', '동물보호법 전면 개정을 위한 시민 서명', '동물권,범죄/사법', '동물학대에 대한 처벌을 강화하고 사각지대를 해소하기 위한 동물보호법 전면 개정을 요구하는 서명입니다.', 'https://example.com/sign/animal1', false, 1, '2023-04-01 00:00:00', '2023-05-20 23:59:59', '서울특별시', '동대문구'),
(1, '서명', '성소수자 차별금지 제도 마련 촉구 서명', '성소수자,인권', '성소수자에 대한 차별을 막고 평등한 권리를 보장하는 제도 마련을 촉구하는 시민 서명입니다.', 'https://example.com/sign/lgbt1', false, 1, '2024-02-15 00:00:00', '2024-04-10 23:59:59', '경기도', '수원시'),
(1, '서명', '동물 실험 축소와 대체시험법 확대 서명', '동물권,의료', '불필요한 동물 실험을 줄이고 대체시험법을 확대하기 위한 정책 변화를 요구하는 서명 운동입니다.', 'https://example.com/sign/animal2', false, 1, '2025-08-01 00:00:00', '2025-09-30 23:59:59', '대구광역시', '서구'),
(1, '서명', '기초생활보장제도 개선 서명', '복지,인권', '기초생활보장제도의 기준 완화와 지원 확대를 요구하는 시민 서명입니다. 최소한의 삶은 권리이며, 제도는 현실을 따라가야 합니다.', 'https://example.com/sign/2023-01', false, 1, '2023-02-01 00:00:00', '2023-03-15 23:59:59', '서울특별시', '은평구'),
(1, '서명', '장애인 돌봄 복지 확대 서명', '복지,장애인', '장애인 돌봄 공백을 해소하고 국가 책임을 강화하기 위한 서명 운동입니다. 가족에게만 떠넘기는 돌봄을 바꿔야 합니다.', 'https://example.com/sign/2023-02', false, 1, '2023-08-10 00:00:00', '2023-09-30 23:59:59', '서울특별시', '동작구'),
(1, '서명', '노동자 복지 후퇴 반대 서명', '복지,노동자', '노동자 복지 축소에 반대하고 고용 형태와 무관한 사회안전망 강화를 요구하는 서명입니다. 일하는 사람이 무너지지 않아야 합니다.', 'https://example.com/sign/2024-01', false, 1, '2024-05-20 00:00:00', '2024-07-10 23:59:59', '대구광역시', '달서구'),
(1, '서명', '청소년 복지 예산 확대 서명', '복지,청소년', '청소년 복지 예산 확대와 정책 강화를 촉구하는 서명 운동입니다. 상담·돌봄·주거 지원 등 실질적인 안전망이 필요합니다.', 'https://example.com/sign/2025-01', false, 1, '2025-09-01 00:00:00', '2025-10-20 23:59:59', '강원특별자치도', '춘천시'),
(1, '서명', '여성 복지 정책 강화 서명', '복지,여성', '여성의 빈곤과 돌봄 부담 해소를 위한 복지 정책 강화를 요구하는 서명입니다. 생계·주거·돌봄을 함께 개선해야 합니다.', 'https://example.com/sign/2026-01', false, 1, '2026-01-05 00:00:00', '2026-02-20 23:59:59', '경상북도', '포항시'),
(1, '서명', '[여성폭력 방지 및 피해자 지원 예산 감축을 막기 위한 1만인 시민 선언]', '여성,인권', '우리는 성평등을 퇴보시키고 피해자를 제대로 지원할 수 없는 예산안에 반대합니다. 우리는 2024년 여성가족부 예산안 전면 백지화를 요구합니다.여성가족부 예산안이 국회 심의에서 전면 폐기될 수 있도록 1만인 시민 선언에 함께 해주세요!', 'https://docs.google.com/forms/d/1595h6xTsQSSWMHpE7Hz-dMWhQs6TVFyKXTxw-NOh6do/closedform', false, 1, '2023-10-27 00:00:00', '2023-10-29 23:59:59', '부산광역시', '중구'),
(1, '서명', '2025 R-DASH 불법 성착취 사이트 근절 대국민 서명', '인권,범죄/사법', '올해 상반기 리셋이 "Y" 사이트에서 채증한 성착취물은 모두 2,485개. 그러나 신고 건에 대해 경찰과 방심위 모두 침묵으로 일관하는 중입니다. 따라서 불법 성착취 사이트의 근절을 촉구하는 대국민 연서명을 받고자 합니다.', 'https://docs.google.com/forms/d/e/1FAIpQLScISvWfWV0dbEfc2E-AVCwpWZLbnGyFOHo-U38rEt0GROOLrA/closedform', false, 1, '2025-10-13 10:30:00', '2025-11-11 10:30:00', '부산광역시', '사하구'),
(1, '서명', '방송법 수용! 이동관 탄핵! 언론탄압 저지를 위한 백만인 서명 운동', '인권,범죄/사법', '방송장악 분쇄! 언론탄압 저지를 위한 방송법 수용! 이동관 탄핵! 백만인 서명 운동에 동참해 주십시오. 시민 여러분 안녕하신지요. 저희는 "언론장악 저지와 이동관 탄핵 공동행동"(준)입니다. 윤석열 정권의 언론장악에 반대하고 이동관 탄핵을 요구하는 시민사회와 노동자, 언론인들의 연대체입니다.', 'https://docs.google.com/forms/d/e/1FAIpQLSdreaiz1lIgTjLVTLkaUCXHypTNnZyn_hU1Fv1Kx-kFzYHfvA/viewform', false, 1, '2023-11-10 00:00:00', '2023-12-10 23:59:59', '부산광역시', '해운대구'),
(1, '서명', '서울지방경찰청은 고 장제원 전 의원의 성폭력 사건, 수사 종결 말고 성폭력 가해 사실 공식 발표하라!', '범죄/사법,인권', '[36시간 긴급 연명] 피의자 사망으로 성폭력 사건의 실체가 묻혀질 수는 없다. 서울지방경찰청은 고 장제원 전 의원의 성폭력 사건, 수사 종결 말고 성폭력 가해 사실 공식 발표하라!', 'https://docs.google.com/forms/d/e/1FAIpQLSeZ2Cj43KHrNsaOkPVwvfmCBnUR6HaYhXFJEELLBbVpKAKSzw/closedform', false, 1, '2025-04-07 18:00:00', '2025-04-09 06:00:00', '대구광역시', '중구'),
(1, '서명', '늦어도 괜찮습니다! 택배노동자 폭염대책 촉구 서명운동', '노동자,인권', '[긴급 범시민 서명운동 제안] 늦어도 괜찮습니다! 택배노동자의 생명과 안전을 지키기 위한 폭염 대책 마련을 촉구합니다. 택배노동자들의 생명과 안전을 바라는 국민들과 시민사회, 노동계, 진보정당 모두의 힘을 모아 폭염 피해를 막아냅시다.', 'https://docs.google.com/forms/d/e/1FAIpQLSdeebhlnQKSKENJIaHCBL55PqPDDOIs2JPkKh-fBzJ88ycF_Q/viewform', false, 1, '2025-07-11 00:00:00', '2025-08-14 23:59:59', '대구광역시', '달서구'),
(1, '서명', '선고 지연 헌법재판소에 대한 윤석열 즉각 파면 촉구 전국 시민 서명', '범죄/사법', '선고 지연 헌법재판소에 대한 윤석열 즉각 파면 촉구 전국 시민 서명. 정의에는 중립이 없습니다. 지연된 정의는 정의가 아니라 불의입니다. 헌법재판소에게 내란 우두머리 윤석열의 파면을 촉구하는 서명캠페인에 함께 해주세요!', 'https://docs.google.com/forms/d/e/1FAIpQLScLYVPMROzrklNtQEbtWVqnWcuqx-jAWotofeRHoB13ikaqZw/closedform', false, 1, '2025-03-30 00:00:00', '2025-04-01 23:59:59', '인천광역시', '부평구'),
(1, '서명', '전교조 서울지부에 지혜복 조합원에 대한 지지를 다시 촉구합니다', '노동자,인권', '공대위는 전교조 서울지부 결정에 유감을 표하며, 지혜복 조합원에 대한 지지를 다시 촉구합니다. 지혜복 교사의 고통스러운 투쟁이 어느덧 500일을 훌쩍 넘었습니다. 공대위는 전교조 서울지부와 함께 싸워 함께 승리하기를 바랍니다. 전교조 서울지부에 지혜복 조합원에 대한 지지를, 다시 간곡히 촉구합니다.', 'https://docs.google.com/forms/d/e/1FAIpQLSeniQhk3eBO38V-zsTfjyBJfEOo7IOWxD_vQshczCZ_ymHJKg/viewform', false, 1, '2025-07-15 08:00:00', '2025-07-22 20:00:00', '인천광역시', '연수구'),
(1, '서명', '안전한 노동, 행복한 급식 학교급식법 전면 개정으로 조리인력 최저기준 법제화를 위한 100만 청원운동', '노동자,인권', '열악한 노동환경으로 사람들이 더는 학교급식 일을 하려고 하지 않아 위탁급식을 하려 하는 위기 상황입니다. 우리가 K급식이라며 자랑하는 학교급식의 위상도 급식노동자가 쓰러지면 무너집니다. 부디 국회가 학교급식법을 전면 개정하여 급식노동자의 생명과 안전을 보장하고, 최저임금처럼 <최저 조리인력 기준>을 대통령령으로 마련하는데 힘을 보태주세요!', 'https://docs.google.com/forms/d/e/1FAIpQLSc4DfJfozNuD6bac4cvyMHm6VAYc1U1_B2BCHXstvALeybCfQ/viewform', false, 1, '2025-09-16 00:00:00', '2025-12-30 23:59:59', '광주광역시', '북구'),
(1, '서명', '청소년도 시민이다! 공공자전거 탑허 허용하라!', '청소년,인권', '[공동성명] 청소년도 시민이다! 공공자전거 탑승 허용하라! 시민이 아니라고 인정하는 시청과 시의회를 규탄한다! 청소년에게도 공공자전거 탑승을! 공동성명에 참여해주세요', 'https://docs.google.com/forms/d/e/1FAIpQLSciZ6j6rZm_1-TIswR0ISwqB_w2-f920q4vwj3NKxzBAHXjEA/closedform', false, 1, '2025-11-03 14:00:00', '2025-11-27 14:00:00', '광주광역시', '동구'),
(1, '서명', '학교에도 언론의 자유를!', '청소년,인권', '우리는 청소년이자 시민으로서, 교육청이 말뿐인 민주주의를 멈추고, 행동하는 민주주의로 나아가길 요구합니다. 우리는 "입틀막"을 거부합니다. 언론의 자유는 교문 앞에서 멈춰서는 안 됩니다. 민주주의는 교실 안에서부터 시작되어야 합니다. 학교가 민주주의를 되찾고, 학생이 기본권을 되찾으려면 여러분의 도움이 필요합니다. 서명을 통해 저희를 응원해 주십시오.', 'https://campaigns.do/campaigns/1695', false, 1, '2025-10-18 03:00:00', '2025-11-24 03:00:00', '대전광역시', '유성구'),
(1, '서명', '나는봄 운영종료 반대 서명', '청소년,인권', '위기의 순간에 필요한 보호와 지원의 안전망을 해체하는 것은 공공 책임을 포기하는것입니다. 서울시는 운영 종료 결정을 즉각 철회하고, 조례와 지침에 따른 절차를 이행하고, 이용자 보호와 종사자 고용승계 대책을 마련해야하며, 민간위탁 기관을 "죽이는" 졸속 행정을 멈추고 위기청소년의 건강권·인권 보호를 최우선으로 해 주실 것을 간절히 호소합니다.', 'https://docs.google.com/forms/d/e/1FAIpQLSefxIhzyzZDvkWryVSXzPZPylDJLx38wr8ZsmYvk9GzrriyoA/viewform', false, 1, '2025-03-15 16:00:00', '2025-06-30 16:00:00', '대전광역시', '서구'),
(1, '청원', '유방암 치료제 엔허투의 건강보험 승인 촉구에 관한 청원', '의료,인권', '유방암 환자에게 마지막 희망으로 불리는 항암제 "엔허투"가 국내 허가를 획득하였지만, 한 번 주사에 약 500만원이 드는 상황입니다. 이에 엔허투는 국내에서 치료제 허가를 받았지만, 실질적으로 사용하지 못하고 있는 것이 현실입니다. 따라서 조속히 유방암 치료제 엔허투의 건강보험 승인을 촉구합니다.', 'https://petitions.assembly.go.kr/status/onGoing/F0F9D69ADF4E69FDE054B49691C1987F', false, 1, '2023-01-30 00:00:00', '2023-02-03 23:59:59', '울산광역시', '남구'),
(1, '청원', '의료민영화를 도모하는 보건복지부의 건강보험정책심의위원회 구성에 관한 청원', '인권,의료', '우리 국민은 대한민국 헌법 제34조와 제36조에 위배되는, 무엇보다 인간다운 생활을 할 국민의 기본권인 사회권을 침해하는 의료민영화에 대해 반대합니다. 따라서 민간보험노조 공문 발송을 필두로 한 조규홍 보건복지부 장관의 의료민영화 시도는 중단되어야 마땅합니다.', 'https://petitions.assembly.go.kr/proceed/registered/28801A272EED4045E064B49691C6967B', false, 1, '2024-12-13 16:00:00', '2025-01-12 16:00:00', '울산광역시', '북구'),
(1, '청원', '산모 사망을 방지하기 위한 의료제도 개선 요청에 관한 청원', '의료,인권', '산모와 태아는 가장 안전한 환경에서 보호받아야 하며, 의료진은 그 책임을 다해야 합니다. 그러나 현재의 제도와 시스템은 이런 비극을 막기에는 너무나 허술합니다. 이번 청원은 단순히 한 사건의 진상 규명에 그치지 않고, 앞으로 같은 일이 반복되지 않도록 제도와 환경을 개선하자는 절박한 요청입니다.', 'https://petitions.assembly.go.kr/proceed/onGoingAll/3A835488B06B0539E064ECE7A7064E8B', false, 1, '2025-08-12 05:00:00', '2025-09-11 05:00:00', '경기도', '수원시'),
(1, '청원', '학교 내 CCTV 설치 의무화 반대에 관한 청원', '인권,교육', '최근 국회에서는 초·중등학교 내 CCTV 설치를 의무화하는 법률 개정안이 발의되었습니다. 이는 학생 안전을 이유로 추진되고 있으나, 교사와 학생 모두의 기본권을 침해하고 교육활동을 위축시킬 수 있다는 심각한 우려가 제기되고 있습니다. 교육청과 교원단체들 또한 무분별한 CCTV 확대는 교육현장에 새로운 갈등과 불신을 조장한다고 경고하였습니다.', 'https://petitions.assembly.go.kr/proceed/onGoingAll/3F27A9DFA9DB090BE064ECE7A7064E8B', false, 1, '2025-09-29 00:00:00', '2025-11-30 23:59:59', '경기도', '고양시'),
(1, '청원', '조희대 대법원장 탄핵에 관한 청원', '범죄/사법', ' 우리 헌법은 국민주권과 삼권분립을 근본 원리로 삼고 있습니다. 그러나 조희대 대법원장은 대선을 앞두고 대선 후보였던 이재명 대통령의 공직선거법 위반 사건을 파기환송했고 이례적인 속도로 재판을 추진하여 정치 개입, 대선 개입의 의혹이 제기된 바 있습니다.', 'https://petitions.assembly.go.kr/proceed/onGoingAll/3A83EBF8D6ED4C7CE064B49691C6967B', false, 1, '2025-09-23 00:00:00', '2025-10-26 23:59:59', '경기도', '성남시'),
(1, '청원', '아동학대범죄의 처벌 등에 관한 특례법 일부개정법률안', '범죄/사법,교육', '교육감 의견제출 제도는 교원을 대상으로 한 무분별한 아동학대 신고로 인해 정당한 교육활동과 생활지도가 위축되는 것을 막기 위하여 도입되었음. 그러나 현행법 제24조는 "사법경찰관아동학대범죄를 신속히 수사하여 사건을 검사에게 송치하여야 한다"고 정하고 있어 아동학대 사안의 경우에는 경찰 수사가 개시되면 수사 결과 혐의 여부와 무관하게 검찰 송치가 불가피한 상황임.', 'https://pal.assembly.go.kr/napal/search/lgsltpaSearch/view.do?lgsltPaId=PRC_C2D5B0C1Y1W3W1U0V0U7U0C1B0B2Z5&searchConClosed=0&refererDiv=S', false, 1, '2025-01-17 00:00:00', '2025-01-26 23:59:59', '경기도', '안양시'),
(1, '청원', '헌법과 법률을 유린한 국민의힘 정당 해산에 관한 청원', '범죄/사법', '대한민국 헌법은 민주주의와 법치주의를 바탕으로 국민의 뜻을 반영하는 정치를 보장합니다. 그러나 2024년 12월 7일 윤석열 대통령 탄핵소추안 표결에서 국민의힘 소속 국회의원들이 조직적으로 투표를 보이콧한 행위는 헌법과 법률을 명백히 위반한 것입니다. 이는 국회의원으로서의 기본 책무를 저버렸을 뿐만 아니라, 정당으로서 민주주의 기본 질서를 훼손한 행위로 간주됩니다.', 'https://petitions.assembly.go.kr/proceed/onGoingAll/288008C178403F22E064B49691C6967B', false, 1, '2024-12-09 00:00:00', '2025-01-08 23:59:59', '경기도', '평택시'),
(1, '청원', '공공복지 예산 삭감 철회 청원', '복지,인권', '공공복지 예산 삭감을 중단하고 사회적 약자를 보호할 것을 요구하는 청원입니다. 예산은 국가의 책임을 보여주는 지표입니다.', 'https://example.com/petition/2023-01', false, 1, '2023-11-01 00:00:00', '2023-12-31 23:59:59', '인천광역시', '중구'),
(1, '청원', '장애인 복지시설 확충 청원', '복지,장애인', '장애인 복지시설 확충과 접근성 강화를 요구하는 청원입니다. 지역별 격차를 줄이고 누구나 이용 가능한 시설이 필요합니다.', 'https://example.com/petition/2024-01', false, 1, '2024-01-15 00:00:00', '2024-03-01 23:59:59', '울산광역시', '남구'),
(1, '청원', '노동자 사회안전망 강화 청원', '복지,노동자', '비정규직과 플랫폼 노동자를 포함한 사회안전망 강화를 요구하는 청원입니다. 일하는 사람이 아프면 바로 무너지는 구조를 바꿔야 합니다.', 'https://example.com/petition/2024-02', false, 1, '2024-10-10 00:00:00', '2024-12-05 23:59:59', '전라남도', '여수시'),
(1, '청원', '청소년 복지 기본권 보장 청원', '복지,청소년', '청소년의 생존과 성장을 위한 복지 기본권 보장을 요구하는 청원입니다. 가정환경과 무관하게 안전망이 작동해야 합니다.', 'https://example.com/petition/2025-01', false, 1, '2025-03-03 00:00:00', '2025-05-01 23:59:59', '세종특별자치시', '세종시'),
(1, '청원', '여성 빈곤 해소 복지 대책 청원', '복지,여성', '여성 빈곤 문제 해결을 위한 국가 차원의 복지 대책을 요구하는 청원입니다. 경력단절과 돌봄 부담이 빈곤으로 이어지지 않아야 합니다.', 'https://example.com/petition/2026-01', false, 1, '2026-02-01 00:00:00', '2026-03-10 23:59:59', '충청남도', '천안시'),
(1, '청원', '성소수자 혐오범죄 대응 체계 마련 청원', '성소수자,범죄/사법', '성소수자를 대상으로 한 혐오범죄를 예방하고 대응하기 위한 법·제도 마련을 요구하는 청원입니다.', 'https://example.com/petition/lgbt1', false, 1, '2024-07-01 00:00:00', '2024-08-31 23:59:59', '인천광역시', '연수구'),
(1, '청원', '동물학대 전담 수사기구 설치 청원', '동물권,범죄/사법', '동물학대 범죄를 전문적으로 수사할 전담 기구 설치를 요구하는 시민 청원입니다.', 'https://example.com/petition/animal1', false, 1, '2025-03-10 00:00:00', '2025-05-01 23:59:59', '충청남도', '천안시'),
(1, '탄원', '전주 M초 악성민원인, 전교조 전북지부와 전교조초등위원회에서 공동으로 형사고발합니다. 엄벌촉구 탄원에 함께해주세요', '범죄/사법,교육', '교육감에게 악성민원인을 추가 고발하라고 반 년을 넘게 요구했습니다. 악성민원인은 오히려 송욱진 선생님을 대상으로 민사소송을 예고했습니다. 악성민원인에 대한 아무런 법적 제재가 없는 지금, 전교조 전북지부는 악성민원인을 형사고발하려 합니다. M초의 교직원과 학생, 학부모들도 결국은 악성민원의 피해자입니다. 교사, 학생, 학부모 모두 탄원에 동의하실 수 있어요', 'https://docs.google.com/forms/d/e/1FAIpQLSfFmAkT3uHdeNATW9WlV1pxYk7IKjaLKAXJj4SWIPrBqxTBEg/viewform', false, 1, '2025-07-17 00:00:00', '2025-08-16 23:59:59', '경기도', '파주시'), 
(1, '탄원', '광양 고양이 포획 학대 사건 엄벌 탄원서', '동물권,범죄/사법', '광양 명당공원에서 고양이 4마리를 학대한 혐의로 카라에서 고발한 박 씨가, 검찰의 5백만원 벌금형 약식 처분에도 불복하여 정식 재판을 청구했습니다. 박 씨는 법무법인을 통해 변호사를 5명이나 선임한 상태입니다. 누구든지 동물을 죽일 목적으로 포획하는 행위는 그 자체로 동물학대 범죄이며, 최대 2년의 징역형 또는 2천만원의 벌금형 대상입니다.', 'https://campaigns.do/campaigns/1669', false, 1, '2025-09-01 00:00:00', '2025-11-18 23:59:59', '전라남도', '광양시'),
(1, '탄원', '다큐멘터리는 범죄가 아닙니다. 정윤석 다큐멘터리 감독의 무죄를 촉구합니다.', '인권,범죄/사법', '다큐멘터리는 범죄가 아닙니다. 정윤석 다큐멘터리 감독의 무죄를 요청합니다. 법원이 처벌해야 할 것은 폭동자입니다. 정윤석 다큐멘터리 감독이 아닙니다. 폭력 현장의 진실을 기록하는 다큐멘터리 감독은 처벌되어서는 안됩니다. 정윤석 감독의 무죄를 촉구합니다.', 'https://docs.google.com/forms/d/e/1FAIpQLSdeE-Pd1oIzlDIyfVTCkZ2GzUByrH8tM3uVDQHtRaRHWVP7-A/viewform', false, 1, '2025-12-24 00:00:00', '2026-01-31 23:59:59', '강원특별자치도', '원주시'),
(1, '탄원', '경남 고성 치매노인 성폭력 엄벌탄원', '범죄/사법,인권', '경남 고성군의 전 이장이 이웃의 80대 치매 노인을 성폭력했습니다. 경찰 진술 당시 범행을 시인했던 가해자는 거대 로펌을 선임한 후 내연관계였다며 피해자를 모욕하고 마을을 선동하고 있습니다. 탄원서 연명으로 사건의 진실을 밝히고 피해자분의 명예를 지키는 데 동참해주십시오.', 'https://docs.google.com/forms/d/e/1FAIpQLScpNUPL_BUOyv6Ua5jNXa5kD9_Az2VqtHiAzuR6HJlnwsYi_g/viewform', false, 1, '2025-10-30 00:00:00', '2026-01-15 23:59:59', '강원특별자치도', '속초시'),
(1, '탄원', '동덕여자대학교가 여성 대학으로 남을 수 있게 함께 싸워온 학생들이 불기소처분을 받을 수 있도록 탄원에 함께해 주십시오.', '인권,범죄/사법', '학교와의 긴 대화 끝에 2025년 5월 학교 본부는 경찰에 형사 고소 취하 의견을 밝혔고, 학생들에 대한 처벌 불원서를 제출하였습니다. 그럼에도 경찰은 일부 학생들을 기소 의견으로 검찰 송치하여 현재 서울북부지방검찰청에서 사건을 수사 중입니다. 학교의 불통에 맞서 용기를 내었던 학생들의 검찰 송치는 동덕여자대학교 학생 사회의 큰 퇴보를 의미하고 있습니다. 안타까운 상황에서 보다 많은 분의 연대를 위해 탄원서를 받고자 합니다.', 'https://docs.google.com/forms/d/e/1FAIpQLSfqQu4cX_NII47yo8iPOfRQ8lwuUZ2C-f3IycBrqBxPm_H3IQ/viewform', false, 1, '2025-08-04 00:00:00', '2025-08-22 23:59:59', '충청북도', '청주시'),
(1, '탄원', '성소수자 차별 피해 사건 엄정 판결 촉구 탄원', '성소수자,인권', '성소수자에 대한 명백한 차별과 혐오 범죄에 대해 법원이 엄정한 판단을 내려줄 것을 요청하는 시민 탄원입니다.', 'https://example.com/appeal/lgbt1', false, 1, '2026-01-05 00:00:00', '2026-02-05 00:00:00', '서울특별시', '서초구'),
(1, '탄원', '여성폭력 피해자 보호 강화를 촉구하는 탄원', '여성,인권', '여성폭력 피해자가 안전하게 보호받고 충분한 지원을 받을 수 있도록 사법기관과 행정기관의 책임 있는 조치를 촉구하는 시민 탄원입니다.', 'https://action.example.org/petition/2024-women-safety', false, 1, '2024-03-01 00:00:00', '2024-04-15 23:59:59', '부산광역시', '연제구'),
(1, '탄원', '청소년 표현의 자유 침해 중단을 요구하는 탄원', '청소년,인권', '학교와 공공기관에서 발생하는 청소년 표현의 자유 침해를 중단하고 권리 보장을 강화할 것을 요구하는 탄원입니다.', 'https://action.example.org/petition/2024-youth-expression', false, 1, '2024-05-10 00:00:00', '2024-06-30 23:59:59', '광주광역시', '동구'),
(1, '탄원', '산업재해 책임자 엄벌을 촉구하는 탄원', '노동자,범죄/사법', '반복되는 산업재해에 대해 책임자를 엄중히 처벌하고 노동자의 생명과 안전을 보호할 것을 요구하는 탄원입니다.', 'https://action.example.org/petition/2023-industrial-safety', false, 1, '2023-11-20 00:00:00', '2023-12-31 23:59:59', '울산광역시', '남구'),
(1, '탄원', '성소수자 차별 사건 공정 수사를 요구하는 탄원', '성소수자,범죄/사법', '성소수자를 대상으로 한 차별과 혐오 사건에 대해 공정하고 투명한 수사를 촉구하는 시민 탄원입니다.', 'https://action.example.org/petition/2025-lgbtq-justice', false, 1, '2025-02-05 00:00:00', '2025-03-31 23:59:59', '대구광역시', '중구'),
(1, '탄원', '농민 생존권 보장을 위한 정책 전환 촉구 탄원', '농민', '농산물 가격 폭락과 농가 부채 문제로 위기에 처한 농민의 생존권을 보장하기 위한 정책 전환을 요구하는 탄원입니다.', 'https://action.example.org/petition/2024-farmers-rights', false, 1, '2024-09-15 00:00:00', '2024-11-15 23:59:59', '전라북도', '정읍시'),
(1, '탄원', '장애인 이동권 침해 사건에 대한 시정 조치 탄원', '장애인,인권', '장애인의 이동권을 침해한 공공시설 운영에 대해 즉각적인 시정과 재발 방지를 요구하는 탄원입니다.', 'https://action.example.org/petition/2023-disability-mobility', false, 1, '2023-12-01 00:00:00', '2024-01-31 23:59:59', '인천광역시', '미추홀구'),
(1, '탄원', '교육 현장 과도한 징계 철회를 요구하는 탄원', '교육', '학생과 교사에게 과도하게 적용되는 징계 조치를 재검토하고 교육적 해결을 촉구하는 탄원입니다.', 'https://action.example.org/petition/2025-education-fairness', false, 1, '2025-04-18 00:00:00', '2025-06-10 23:59:59', '경기도', '안산시'),
(1, '탄원', '환경 파괴 개발 사업 중단을 촉구하는 탄원', '환경,인권', '지역 생태계를 파괴하고 주민의 삶을 위협하는 개발 사업을 중단할 것을 요구하는 시민 탄원입니다.', 'https://action.example.org/petition/2024-environment-defense', false, 1, '2024-06-30 00:00:00', '2024-08-20 23:59:59', '강원특별자치도', '강릉시'),
(1, '탄원', '필수의료 공백 해소를 요구하는 탄원', '의료,복지', '지역 필수의료 공백으로 인한 시민 피해를 막기 위해 의료 인력 확충과 제도 개선을 요구하는 탄원입니다.', 'https://action.example.org/petition/2025-essential-healthcare', false, 1, '2025-01-12 00:00:00', '2025-03-05 23:59:59', '충청북도', '제천시'),
(1, '탄원', '동물학대 사건에 대한 엄정 판결 촉구 탄원', '동물권,범죄/사법', '반복되는 동물학대 사건에 대해 법원이 엄정한 판결을 내릴 것을 촉구하는 시민 탄원입니다.', 'https://action.example.org/petition/2023-animal-cruelty', false, 1, '2023-08-25 00:00:00', '2023-10-10 23:59:59', '경상남도', '양산시'),
(1, '탄원', '10.29 참사 진상규명과 책임자 처벌을 요구하는 탄원', '추모/기억,범죄/사법', '10.29 참사의 희생자를 기억하며 철저한 진상규명과 책임자 처벌을 요구하는 시민 탄원입니다.', 'https://action.example.org/petition/2024-1029-truth', false, 1, '2024-10-29 00:00:00', '2024-12-20 23:59:59', '부산광역시', '중구');
INSERT INTO boards ( user_id, participation_type, title, topics, content, link, is_verified, ai_verified, start_date, end_date, region, district ) VALUES 
(1, '집회', '2025 기후위기 비상행동 결의대회', '환경', '지구 온난화 한계선 1.5도가 무너지고 있습니다. 탄소 배출 감축과 신규 석탄화력발전소 중단을 강력히 요구하는 전국 시민 집회를 개최합니다.', 'https://climate-action.kr/rally', false, 1, '2025-09-21 14:00:00', '2025-09-21 17:00:00', '서울특별시', '종로구'),
(1, '행사', '디지털 성폭력 예방 및 피해자 지원 가이드 강연', '여성,인권', '딥페이크 성착취물 등 고도화되는 디지털 범죄의 실태를 파악하고, 피해 발생 시 법적 대응 절차와 심리 치유 방법을 전문가와 함께 알아봅니다.', 'https://stop-digital-crime.org/guide', false, 1, '2025-10-15 19:00:00', '2025-10-15 21:00:00', '경기도', '수원시'),
(1, '서명', '플랫폼 노동자 휴게 시설 의무화 촉구 서명', '노동자,복지', '배달, 택배 기사 등 이동 노동자들이 폭염과 한파를 피할 수 있는 공공 휴게실 설치를 지자체와 플랫폼 기업에 요구합니다.', 'https://platform-workers.kr/rest', false, 1, '2025-07-01 00:00:00', '2025-08-31 23:59:59', '서울특별시', '강남구'),
(1, '청원', '군 내 가혹행위 근절 및 독립 수사 기구 설치 청원', '인권,범죄/사법', '군대 내 폐쇄적인 수사 구조가 비극을 반복시키고 있습니다. 군 인권 보호를 위한 민간 수사 기구 설치 특별법 제정을 청원합니다.', 'https://petitions.assembly.go.kr/military-humanrights', false, 1, '2025-03-12 09:00:00', '2025-04-12 18:00:00', '서울특별시', '용산구'),
(1, '탄원', '특성화고 현장실습생 안전 사고 책임자 엄벌 탄원', '청소년,노동자', '안전 대책 없이 학생들을 위험한 현장으로 내모는 관행을 멈춰야 합니다. 현장실습 도중 발생한 사고의 책임자 처벌을 강력히 요청합니다.', 'https://youth-labor.org/appeal', false, 1, '2025-11-10 00:00:00', '2025-12-10 23:59:59', '전라북도', '전주시'),
(1, '집회', '최저임금 차등 적용 반대 및 생활임금 보장 집회', '노동자', '업종별 차등 적용은 노동자 갈라치기입니다. 물가 상승률을 반영한 실질 임금 인상과 모두를 위한 보편적 생활임금 보장을 촉구합니다.', 'https://labor-justice.kr/wage', false, 1, '2025-05-01 13:00:00', '2025-05-01 16:00:00', '서울특별시', '중구'),
(1, '행사', '반려동물 사지 말고 입양하세요 : 유기동물 입양제', '동물권', '가족을 기다리는 유기동물들을 만나보세요. 올바른 반려 문화 정착을 위한 교육과 함께 새로운 인연을 맺는 캠페인을 진행합니다.', 'https://adopt-pets.or.kr/festival', false, 1, '2025-04-20 10:00:00', '2025-04-20 17:00:00', '경기도', '성남시'),
(1, '서명', '노인 무임승차 폐지 반대 및 교통 복지 사수 서명', '복지,인권', '초고령 사회, 노인들의 유일한 이동 수단인 지하철 무임승차를 비용의 논리로만 접근해서는 안 됩니다. 보편적 교통 권리 보장을 요구합니다.', 'https://silver-rights.kr/mobility', false, 1, '2025-06-15 00:00:00', '2025-07-15 23:59:59', '서울특별시', '서초구'),
(1, '청원', '아동 학대 전담 공무원 확충 및 보호 체계 개선 청원', '복지,청소년', '현장 인력 부족으로 아이들이 방치되고 있습니다. 전문성을 갖춘 전담 인력을 증원하고 즉각적인 분리 조치 환경을 조성하십시오.', 'https://protect-children.kr/petition', false, 1, '2025-02-05 00:00:00', '2025-03-05 23:59:59', '대전광역시', '서구'),
(1, '탄원', '기후위기 소송 활동가 무죄 및 국가 책임 인정 탄원', '환경,인권', '미래 세대의 생존권을 위해 국회와 정부 앞에서 행동한 청년들의 행위는 정당합니다. 이들에 대한 공소 취하와 기후 정의 실현을 탄원합니다.', 'https://climate-litigation.kr/appeal', false, 1, '2025-12-24 00:00:00', '2026-01-24 23:59:59', '서울특별시', '종로구'),
(1, '집회', '대형 플랫폼 수수료 인하 및 공정 거래 촉구 집회', '복지,노동자', '배달앱 수수료 횡포로 소상공인들이 쓰러지고 있습니다. 거대 플랫폼의 독과점을 규제하고 상생을 위한 법적 장치를 마련하십시오.', 'https://fair-platform.kr/action', false, 1, '2025-08-18 15:00:00', '2025-08-18 17:00:00', '서울특별시', '송파구'),
(1, '행사', '마을 공동체 돌봄 모델 확산을 위한 정책 포럼', '복지,여성', '독박 육아를 넘어 온 마을이 아이를 함께 키우는 공동체 돌봄 시스템의 성공 사례를 공유하고 지자체 예산 지원 방안을 논의합니다.', 'https://village-care.org/forum', false, 1, '2025-03-25 14:00:00', '2025-03-25 17:00:00', '경기도', '고양시'),
(1, '서명', '일회용 플라스틱 규제 정상화 및 유예 철회 서명', '환경', '환경 정책은 후퇴해서는 안 됩니다. 플라스틱 빨대와 컵 사용 금지 규제를 당장 실시하여 자원 순환 사회로 나아가야 합니다.', 'https://plastic-ban.kr/sign', false, 1, '2025-11-01 00:00:00', '2025-12-31 23:59:59', '서울특별시', '종로구'),
(1, '청원', '장애인 이동권 보장을 위한 저상버스 100% 도입 청원', '장애인,인권', '이동은 권리입니다. 모든 시내버스와 마을버스의 저상화를 의무화하고, 지하철 엘리베이터 설치를 조속히 완료할 것을 청원합니다.', 'https://barrier-free.kr/petition', false, 1, '2025-09-01 00:00:00', '2025-10-01 23:59:59', '부산광역시', '해운대구'),
(1, '탄원', '디지털 소외계층 정보접근성 침해 시정 조치 탄원', '복지,인권', '키오스크와 스마트폰 앱 사용이 필수인 시대, 노인과 장애인이 배제되지 않도록 보조 기기 보급과 교육을 지자체에 탄원합니다.', 'https://digital-access.kr/appeal', false, 1, '2025-01-10 00:00:00', '2025-02-10 23:59:59', '인천광역시', '미추홀구'),
(1, '집회', '공공의료 확대 및 의대 정원 증원 분산 배치 집회', '의료,복지', '수도권 쏠림을 막고 지역 필수의료 공백을 메우기 위해 공공 의과대학 설립과 공공병원의 대대적인 확충을 요구합니다.', 'https://public-health.kr/rally', false, 1, '2025-02-28 13:00:00', '2025-02-28 16:00:00', '서울특별시', '영등포구'),
(1, '행사', '경력단절 여성 재취업 지원 박람회 : 다시 일어서는 나', '여성,노동자', '임신과 육아로 경력이 중단된 여성들에게 맞춤형 일자리 연결과 직업 교육을 제공합니다. 여러분의 새로운 시작을 응원합니다.', 'https://woman-job.kr/expo', false, 1, '2025-06-12 10:00:00', '2025-06-12 18:00:00', '경기도', '안양시'),
(1, '서명', '청년 주거 안정 및 월세 지원 상시화 촉구 서명', '복지', '높은 주거비로 고통받는 청년들을 위해 일시적인 지원이 아닌 보편적 주거 수당 신설과 임대주택 확충을 강력히 요구합니다.', 'https://youth-housing.kr/sign', false, 1, '2025-04-05 00:00:00', '2025-05-05 23:59:59', '서울특별시', '관악구'),
(1, '청원', '불법 스토킹 범죄 처벌 강화 및 피해자 보호 특별법', '인권,범죄/사법', '가해자가 다시 접근하지 못하도록 GPS 위치 추적을 의무화하고 실질적인 신변 보호 조치를 강화하는 법 개정을 청원합니다.', 'https://stop-stalking.kr/petition', false, 1, '2025-10-30 00:00:00', '2025-11-30 23:59:59', '강원특별자치도', '춘천시'),
(1, '탄원', '특정 정당 소속 국회의원의 혐오 발언 징계 탄원', '인권', '소수자와 사회적 약자에 대한 혐오를 부추기는 발언을 일삼는 정치인에 대해 국회 윤리위원회의 엄정한 징계를 탄원합니다.', 'https://stop-hate.kr/appeal', false, 1, '2025-07-22 00:00:00', '2025-08-22 23:59:59', '서울특별시', '종로구'),
(1, '집회', '입시 제도 개혁 및 사교육비 경감 대책 요구 집회', '교육,청소년', '경쟁 위주의 입시 교육이 아이들을 죽이고 있습니다. 공교육 정상화와 대학 서열화 폐지를 통한 교육 혁명을 요구합니다.', 'https://edu-reform.kr/rally', false, 1, '2025-11-15 14:00:00', '2025-11-15 17:00:00', '서울특별시', '강남구'),
(1, '행사', '다문화 가정 자녀를 위한 언어 및 정서 발달 캠프', '교육,인권', '우리 사회의 소중한 구성원인 다문화 아동들이 언어 장벽 없이 꿈을 펼칠 수 있도록 지원하는 멘토링 프로그램을 진행합니다.', 'https://multi-kids.kr/camp', false, 1, '2025-08-10 10:00:00', '2025-08-12 16:00:00', '충청남도', '천안시'),
(1, '서명', '비정규직 차별 철폐 및 동일가치노동 동일임금 서명', '노동자,인권', '일하는 곳이 달라도, 고용 형태가 달라도 하는 일이 같다면 임금도 같아야 합니다. 비정규직을 정규직화하고 차별을 멈추십시오.', 'https://equal-pay.kr/sign', false, 1, '2025-03-01 00:00:00', '2025-04-30 23:59:59', '울산광역시', '남구'),
(1, '청원', '공공 요금 인상 중단 및 서민 물가 안정 특별법 청원', '복지', '난방비, 전기료, 교통비 폭탄에 서민들의 삶이 무너지고 있습니다. 생존권 보장을 위한 공공요금 인상 중단과 바우처 확대를 청원합니다.', 'https://stop-inflation.kr/petition', false, 1, '2025-01-20 00:00:00', '2025-02-20 23:59:59', '서울특별시', '중구'),
(1, '탄원', '아파트 경비 노동자 휴게 시설 지상화 의무화 탄원', '노동자,인권', '지하 창고 같은 곳에서 쉬는 것은 고문입니다. 햇볕이 잘 드는 지상에 경비실 휴게실을 설치하도록 지자체의 명령을 탄원합니다.', 'https://security-rights.kr/appeal', false, 1, '2025-09-15 00:00:00', '2025-10-15 23:59:59', '경기도', '부천시'),
(1, '집회', '후쿠시마 오염수 방류 중단 및 수산물 안전 사수 대회', '환경,인권', '우리의 식탁과 바다 생태계를 위협하는 오염수 방류를 당장 중단시키고 투명한 국제 공동 조사를 실시할 것을 요구합니다.', 'https://clean-ocean.kr/rally', false, 1, '2025-08-05 14:00:00', '2025-08-05 16:00:00', '전라남도', '목포시'),
(1, '행사', '제로 웨이스트(Zero Waste) 일상 실천 워크숍', '환경', '쓰레기 없는 삶, 어렵지 않습니다. 천연 수세미 제작부터 용기 내 캠페인 참여 방법까지 구체적인 실천법을 공유합니다.', 'https://zero-waste.kr/workshop', false, 1, '2025-05-22 15:00:00', '2025-05-22 17:00:00', '부산광역시', '해운대구'),
(1, '서명', '군 병영 시설 현대화 및 병사 급식비 현실화 서명', '인권', '장병들이 국가를 지키는 동안 인간다운 삶을 누릴 수 있도록 노후된 시설을 보수하고 식단 질을 대폭 개선해야 합니다.', 'https://military-welfare.kr/sign', false, 1, '2025-02-15 00:00:00', '2025-03-15 23:59:59', '강원특별자치도', '원주시'),
(1, '청원', '딥페이크 성착취물 제작자 무관용 원칙 엄벌 청원', '여성,범죄/사법', '기술의 발전이 여성을 괴롭히는 도구가 되어서는 안 됩니다. 제작자뿐만 아니라 유포자와 시청자까지 모두 강력히 처벌하십시오.', 'https://stop-deepfake.kr/petition', false, 1, '2025-12-01 00:00:00', '2025-12-31 23:59:59', '서울특별시', '동대문구'),
(1, '탄원', '특정 지역 비하 유튜버 수사 및 강력 처벌 탄원', '인권,범죄/사법', '온라인상에서 근거 없는 혐오로 특정 지역민들을 모욕하고 갈등을 조장하는 유튜버들에 대한 철저한 수사와 구속을 탄원합니다.', 'https://clean-media.kr/appeal', false, 1, '2025-04-10 00:00:00', '2025-05-10 23:59:59', '광주광역시', '서구'),
(1, '집회', '50인 미만 사업장 중대재해처벌법 전면 적용 집회', '노동자', '죽음의 크기가 사업장 규모에 따라 다를 수 없습니다. 모든 노동자가 안전하게 퇴근할 수 있도록 법 유예 시도를 중단하십시오.', 'https://safe-work.kr/rally', false, 1, '2025-01-15 13:00:00', '2025-01-15 15:00:00', '서울특별시', '영등포구'),
(1, '행사', '정신건강 고위험군 긴급 지원 및 24시간 상담 안내', '복지,의료', '외로움과 우울감으로 힘든 이웃들을 위해 지역사회가 함께합니다. 24시간 상담 전화와 전문 심리 지원 프로그램을 소개합니다.', 'https://mental-health.kr/info', false, 1, '2025-09-10 10:00:00', '2025-09-10 12:00:00', '충청북도', '청주시'),
(1, '서명', '도서정가제 강화 및 골목 서점 보호법 찬성 서명', '교육', '문화 다양성의 보루인 동네 서점들이 무너지지 않도록 대형 온-오프라인 서점의 과도한 할인을 규제하고 보호해야 합니다.', 'https://save-bookstore.kr/sign', false, 1, '2025-07-20 00:00:00', '2025-08-20 23:59:59', '전라북도', '군산시'),
(1, '청원', '독거 노인 영양 도시락 및 공공 돌봄 예산 증액 청원', '복지', '한 끼 식사가 생명과 직결되는 홀몸 어르신들이 많습니다. 양질의 도시락 배달 사업을 시군구 전역으로 확대할 것을 청원합니다.', 'https://silver-care.kr/petition', false, 1, '2025-03-20 00:00:00', '2025-04-20 23:59:59', '부산광역시', '사상구'),
(1, '탄원', '학교 폭력 은폐 및 축소 수사 관계자 문책 탄원', '청소년,교육', '가해 학생의 배경에 따라 사건을 축소하려 한 학교 관계자와 수사 기관의 책임을 물어 피해 학생의 억울함을 풀어주십시오.', 'https://school-justice.kr/appeal', false, 1, '2025-10-12 00:00:00', '2025-11-12 23:59:59', '경기도', '고양시'),
(1, '집회', '대중교통 무제한 정기권 전국 확대 촉구 집회', '복지', '지자체별로 쪼개진 정기권을 하나로 합치고, 저소득층과 청년에게는 더 파격적인 혜택을 제공하여 이동권을 보장하십시오.', 'https://metro-pass.kr/rally', false, 1, '2025-02-10 15:00:00', '2025-02-10 17:00:00', '서울특별시', '시청광장'),
(1, '행사', '가사 노동의 가치 인정 및 가사 노동자 권리 안내', '여성,노동자', '보이지 않는 곳에서 우리 사회를 유지해 온 가사 노동자들의 정당한 권리와 4대 보험 적용, 휴게 권리를 함께 알아봅니다.', 'https://house-labor.kr/event', false, 1, '2025-06-16 14:00:00', '2025-06-16 16:00:00', '경상남도', '창원시'),
(1, '서명', '개 식용 금지법 완전 시행 및 업종 전환 지원 서명', '동물권', '동물 학대의 역사를 끝냅시다. 관련 업종 종사자들에게는 정당한 폐업 보상을 지원하고 개 식용을 완전히 금지해야 합니다.', 'https://no-dog-meat.kr/sign', false, 1, '2025-08-30 00:00:00', '2025-09-30 23:59:59', '충청남도', '아산시'),
(1, '청원', '지방 소멸 방지를 위한 공공기관 2차 이전 청원', '복지,인권', '수도권 집중을 해소하고 지역 청년들에게 일자리를 제공하기 위해 남은 공공기관들의 지방 이전을 강력히 추진하십시오.', 'https://local-revive.kr/petition', false, 1, '2025-11-10 00:00:00', '2025-12-10 23:59:59', '전라남도', '나주시'),
(1, '탄원', '산업재해 책임자 구속 수사 및 징벌적 손배 탄원', '노동자,범죄/사법', '노동자의 생명을 돈보다 가볍게 여기는 경영진들에게 엄중한 책임을 물어주십시오. 안전 관리 소홀은 명백한 범죄입니다.', 'https://industrial-safety.kr/appeal', false, 1, '2025-06-28 00:00:00', '2025-07-28 23:59:59', '경상북도', '포항시'),
(1, '집회', '플라스틱 제로 사회를 위한 시민 행진', '환경', '미세플라스틱으로부터 바다와 인간을 지킵시다. 일회용품 규제 강화를 정부에 요구하는 거리 집회를 시작합니다.', 'https://plastic-zero.kr/march', false, 1, '2025-10-04 14:00:00', '2025-10-04 16:30:00', '제주특별자치도', '제주시'),
(1, '행사', '이주노동자 인권 실태 보고회 및 문화 교류의 날', '인권', '우리 곁의 이웃, 이주노동자들이 겪는 차별과 노동 환경의 현실을 공유하고 서로의 문화를 이해하는 소통의 장을 마련했습니다.', 'https://migrant-rights.or.kr/report', false, 1, '2025-05-20 13:00:00', '2025-05-20 17:00:00', '경기도', '안산시'),
(1, '서명', '아동 수당 확대 및 지급 연령 상향 촉구 서명 운동', '복지,청소년', '양육 부담 경감과 아동의 기본권 보장을 위해 아동 수당을 월 30만원으로 확대하고 지급 대상을 만 18세까지 늘려야 합니다.', 'https://child-welfare.kr/sign', false, 1, '2025-06-01 00:00:00', '2025-08-31 23:59:59', '세종특별자치시', '세종시'),
(1, '청원', '공공기관 채용 비리 엄단 및 전수조사에 관한 청원', '범죄/사법,교육', '공정한 기회를 박탈하는 채용 비리는 민주주의의 적입니다. 지난 5년간의 공공기관 채용 과정을 전수조사하고 가해자를 엄벌하십시오.', 'https://fair-chance.kr/petition', false, 1, '2025-11-01 00:00:00', '2025-12-01 23:59:59', '강원특별자치도', '춘천시'),
(1, '탄원', '전세사기 피해자 구제를 위한 특별법 보완 탄원', '인권,복지', '지연된 대책으로 피해자들의 고통이 커지고 있습니다. 실질적인 선구제 후회수 방안을 포함한 법 개정을 강력히 탄원합니다.', 'https://victim-support.kr/appeal', false, 1, '2025-01-15 00:00:00', '2025-02-15 23:59:59', '인천광역시', '미추홀구'),
(1, '집회', '대형마트 의무휴업 유지 및 골목상권 보호 집회', '노동자,복지', '유통 노동자의 건강권과 중소 상인의 생존권을 위해 마트 의무휴업은 반드시 유지되어야 합니다. 상생 없는 규제 완화에 반대합니다.', 'https://market-justice.org/rally', false, 1, '2025-03-22 15:00:00', '2025-03-22 17:00:00', '충청북도', '청주시'),
(1, '행사', '스토킹 범죄 예방을 위한 지역사회 안전망 구축 포럼', '여성,인권', '더 이상 죽이지 마라. 스토킹 범죄의 초기 대응 시스템을 강화하고 피해자 신변 보호를 위한 지자체의 역할을 논의합니다.', 'https://stop-stalking.kr/forum', false, 1, '2025-07-09 14:00:00', '2025-07-09 16:30:00', '경상남도', '창원시'),
(1, '서명', '일본 오염수 방류 중단 및 수산물 안전 확보 서명', '환경,인권', '우리의 식탁과 바다의 생명을 위협하는 방사능 오염수 방류에 반대합니다. 국제적 감시 체계 강화와 중단을 촉구하는 서명에 참여하세요.', 'https://clean-ocean.kr/sign', false, 1, '2025-08-01 00:00:00', '2025-09-30 23:59:59', '전라남도', '목포시'),
(1, '청원', '사형제 폐지 및 대체 형벌 도입에 관한 청원', '인권,범죄/사법', '생명권은 국가가 침해할 수 없는 절대적 가치입니다. 사형제를 공식적으로 폐지하고 가석방 없는 종신형 등 실효적 대안을 마련하십시오.', 'https://human-rights.kr/no-death-penalty', false, 1, '2025-12-10 09:00:00', '2026-01-10 18:00:00', '서울특별시', '서초구'),
(1, '탄원', '특성화고 현장실습생 안전 사고 책임자 엄벌 탄원', '청소년,노동자', '교육이라는 이름 아래 노동 착취와 안전 사고가 반복되고 있습니다. 현장실습 도중 사망한 학생의 억울함을 풀기 위해 책임자를 엄벌에 처해 주십시오.', 'https://youth-labor.org/appeal', false, 1, '2025-11-20 00:00:00', '2025-12-20 23:59:59', '전라북도', '전주시'),
(1, '집회', '학교 폭력 전수 조사 및 실효적 대책 촉구 집회', '교육,청소년', '보여주기식 대책은 끝났다. 학교 폭력 가해자 엄벌과 피해 학생에 대한 완벽한 분리 및 치유 대책을 촉구합니다.', 'https://stop-bullying.kr/rally', false, 1, '2025-04-12 14:00:00', '2025-04-12 16:00:00', '서울특별시', '강남구'),
(1, '행사', '마을 공동체 돌봄 모델 확산 세미나', '복지', '국가 중심 돌봄의 한계를 넘어 마을이 아이를 함께 키우는 공동체 돌봄의 성공 사례를 공유하고 정책화 방안을 찾습니다.', 'https://village-care.org/semi', false, 1, '2025-06-05 15:00:00', '2025-06-05 18:00:00', '경기도', '성남시'),
(1, '서명', '비급여 진료비 통제 및 공공의료 수가 정상화 서명', '의료,복지', '과도한 비급여 진료로 인한 서민 부담을 줄이고, 공공병원이 안정적으로 운영될 수 있는 적정 수가를 보장해야 합니다.', 'https://health-justice.kr/sign', false, 1, '2025-07-01 00:00:00', '2025-08-31 23:59:59', '경상북도', '포항시'),
(1, '청원', '디지털 소외계층 정보접근성 보장법 제정 청원', '인권,복지', '키오스크, 모바일 앱 사용이 어려운 노인과 장애인을 위해 공공기관 및 다중이용시설의 디지털 접근성 의무화를 요구합니다.', 'https://digital-access.kr/petition', false, 1, '2025-09-01 00:00:00', '2025-10-01 23:59:59', '광주광역시', '북구'),
(1, '탄원', '기후위기 소송인단 무죄 탄원 및 국가 책임 인정 촉구', '환경,범죄/사법', '미래 세대의 생존권을 위해 행동한 환경 활동가들의 행위는 정당방위입니다. 탄원서 연명으로 기후 정의를 세워주세요.', 'https://climate-litigation.kr/appeal', false, 1, '2025-10-15 00:00:00', '2025-11-15 23:59:59', '서울특별시', '서초구'),
(1, '집회', '공공운수 노조 총파업 지지 시민 결의대회', '노동자', '철도, 지하철, 에너지 등 공공서비스의 민영화 시도를 저지하고 노동 조건 개선을 통해 시민의 안전을 지킵시다.', 'https://labor-union.org/strike', false, 1, '2025-11-22 13:00:00', '2025-11-22 17:00:00', '서울특별시', '영등포구'),
(1, '행사', '성평등한 명절 문화 만들기 캠페인 발대식', '여성,인권', '가사 노동의 평등한 분담과 성별 고정관념 없는 명절을 위해 시민들이 직접 기획한 캠페인을 시작합니다.', 'https://gender-equal.kr/holiday', false, 1, '2025-09-10 11:00:00', '2025-09-10 13:00:00', '충청남도', '천안시'),
(1, '서명', '군 병영 시설 현대화 및 급식 질 개선 촉구 서명', '인권', '우리 장병들이 최소한의 인간적 대우를 받을 수 있도록 낙후된 시설을 보수하고 식단 예산을 대폭 현실화해야 합니다.', 'https://military-welfare.kr/sign', false, 1, '2025-05-15 00:00:00', '2025-06-15 23:59:59', '강원특별자치도', '원주시'),
(1, '청원', '음주운전 가해자 처벌 강화 및 재범 방지 교육 의무화 청원', '범죄/사법', '살인 행위와 다름없는 음주운전에 대해 법정 최고형을 선고하고, 알코올 중독 치료와 병행된 재범 방지 교육을 법제화하십시오.', 'https://stop-drunk-drive.kr/petition', false, 1, '2025-03-01 00:00:00', '2025-03-31 23:59:59', '부산광역시', '해운대구'),
(1, '탄원', '지역아동센터 운영난 해결을 위한 긴급 예산 지원 탄원', '청소년,복지', '물가 상승에도 불구하고 운영비 지원은 제자리입니다. 아이들의 따뜻한 밥상과 돌봄 공간을 지키기 위한 탄원에 함께해주세요.', 'https://child-center.org/appeal', false, 1, '2025-08-05 00:00:00', '2025-09-05 23:59:59', '전라남도', '나주시'),
(1, '집회', '그린벨트 해제 반대 및 도시 숲 사수 집회', '환경', '무분별한 개발로 인한 녹지 파괴를 멈추십시오. 도시의 허파인 그린벨트를 지켜 시민의 건강권을 사수합시다.', 'https://save-forest.kr/march', false, 1, '2025-11-02 14:00:00', '2025-11-02 16:00:00', '경기도', '과천시'),
(1, '행사', '경력단절 여성 재취업 지원 정책 공청회', '여성,노동자', '출산과 육아로 경력이 중단된 여성들이 다시 사회의 주역이 될 수 있도록 실효성 있는 지원 방안을 모색합니다.', 'https://woman-career.kr/hearing', false, 1, '2025-04-20 14:00:00', '2025-04-20 17:00:00', '인천광역시', '연수구'),
(1, '서명', '청년 주거 수당 신설 및 월세 지원 확대 서명', '복지', '높은 주거비로 고통받는 청년들을 위해 정부 차원의 주거 수당 신설과 월세 지원 대상 대폭 확대를 요구합니다.', 'https://youth-housing.kr/sign', false, 1, '2025-02-10 00:00:00', '2025-03-10 23:59:59', '서울특별시', '관악구'),
(1, '청원', '간호법 제정 및 간호사 1인당 환자 수 제한 청원', '의료,노동자', '의료 현장의 붕괴를 막고 환자 안전을 지키기 위해 전문화된 간호법 제정과 적정 인력 배치를 법제화해야 합니다.', 'https://nurse-act.kr/petition', false, 1, '2025-05-30 00:00:00', '2025-06-30 23:59:59', '대구광역시', '북구'),
(1, '탄원', '발달장애인 참사 재발 방지 및 국가 책임제 촉구 탄원', '장애인,인권', '죽음으로 내몰리는 발달장애인과 그 가족들을 더 이상 외면하지 마십시오. 국가가 책임지는 전 생애 돌봄 체계를 탄원합니다.', 'https://care-justice.kr/appeal', false, 1, '2025-10-25 00:00:00', '2025-11-25 23:59:59', '울산광역시', '남구'),
(1, '집회', '대형 선박 오염물질 배출 규제 강화 촉구 집회', '환경', '항만 지역 미세먼지의 주범인 선박 연료유 규제를 강화하고 친환경 항만 구축을 요구하는 시민 행동입니다.', 'https://clean-port.kr/rally', false, 1, '2025-09-07 13:00:00', '2025-09-07 15:00:00', '부산광역시', '영도구'),
(1, '행사', '다문화 가정 자녀 언어 교육 지원 프로그램 홍보', '교육,복지', '우리 사회의 소중한 아이들이 언어 장벽 없이 성장할 수 있도록 지원하는 맞춤형 교육 프로그램을 소개합니다.', 'https://multicultural-edu.kr/info', false, 1, '2025-03-15 10:00:00', '2025-03-15 12:00:00', '경기도', '평택시'),
(1, '서명', '공영 방송 정치적 독립 보장법 개정 서명', '인권,범죄/사법', '방송이 정권의 전유물이 되지 않도록 공영방송 지배구조 개선과 편집권 독립을 위한 법 개정에 힘을 보태주세요.', 'https://media-freedom.kr/sign', false, 1, '2025-11-05 00:00:00', '2025-12-05 23:59:59', '서울특별시', '마포구'),
(1, '청원', '소규모 자영업자 전기요금 경감 및 에너지 바우처 청원', '복지', '계속되는 경기 불황 속에서 전기요금 등 공공요금 인상은 자영업자들에게 치명적입니다. 긴급 감면 대책을 청원합니다.', 'https://save-shop.kr/petition', false, 1, '2025-01-20 00:00:00', '2025-02-20 23:59:59', '전라북도', '익산시'),
(1, '탄원', '미성년자 성착취물 제작자 무관용 원칙 엄벌 탄원', '범죄/사법,청소년', '아이들의 영혼을 파괴하는 디지털 성착취 범죄에 대해 솜방망이 처벌은 안 됩니다. 법정 최고형으로 사회적 경종을 울려주십시오.', 'https://protect-kids.kr/appeal', false, 1, '2025-06-15 00:00:00', '2025-07-15 23:59:59', '충청북도', '충주시'),
(1, '집회', '노인 빈곤 해결을 위한 기초연금 인상 촉구 집회', '복지', 'OECD 국가 중 노인 빈곤율 1위, 이제는 해결해야 합니다. 기초연금 40만원 조기 인상을 위한 전국 어르신 결의대회.', 'https://silver-welfare.kr/rally', false, 1, '2025-05-10 14:00:00', '2025-05-10 16:00:00', '서울특별시', '종로구'),
(1, '행사', '플로깅(Plogging): 쓰레기 줍고 지구 살리는 조깅', '환경', '함께 뛰며 건강도 챙기고 지역 환경도 정화하는 플로깅 행사에 참여하세요. 생분해 봉투와 집게를 무료로 제공합니다.', 'https://plogging-day.kr/join', false, 1, '2025-10-21 09:00:00', '2025-10-21 12:00:00', '강원특별자치도', '강릉시'),
(1, '서명', '예술인 고용보험 및 창작 지원금 확대 서명', '노동자,인권', '불안정한 수입으로 창작 활동을 포기하는 예술가들이 없도록, 사회보험 적용 확대와 직접 지원금 강화를 촉구합니다.', 'https://artist-rights.kr/sign', false, 1, '2025-02-28 00:00:00', '2025-03-31 23:59:59', '제주특별자치도', '서귀포시'),
(1, '청원', '어린이집 교사 처우 개선 및 대기 시간 유급화 청원', '교육,노동자', '보육의 질은 교사의 행복에서 시작됩니다. 과도한 업무 경감과 정당한 노동 대가를 위해 법률 개정을 청원합니다.', 'https://care-teacher.kr/petition', false, 1, '2025-11-15 00:00:00', '2025-12-15 23:59:59', '경상남도', '진주시'),
(1, '탄원', '전통시장 재개발로 인한 상인 퇴거 반대 탄원', '복지,인권', '수십 년간 지역 상권을 지켜온 영세 상인들을 대책 없이 내쫓는 개발 사업에 반대합니다. 상생 방안 마련을 탄원합니다.', 'https://market-survival.kr/appeal', false, 1, '2025-04-05 00:00:00', '2025-05-05 23:59:59', '충청북도', '청주시'),
(1, '집회', '석탄화력발전소 폐쇄 및 탈석탄법 제정 촉구', '환경', '미세먼지와 온난화의 주범, 석탄화력발전소를 조기에 폐쇄하고 노동자의 전직을 지원하는 탈석탄법을 당장 제정하십시오.', 'https://no-coal.kr/action', false, 1, '2025-09-22 14:00:00', '2025-09-22 17:00:00', '충청남도', '당진시'),
(1, '행사', '성소수자 청소년 상담 및 심리 지원 안내', '성소수자,청소년', '차별과 혐오로 상처받은 퀴어 청소년들을 위한 무료 심리 상담 프로그램을 진행합니다. 당신은 혼자가 아닙니다.', 'https://lgbtq-youth.kr/support', false, 1, '2025-06-01 10:00:00', '2025-06-30 18:00:00', '서울특별시', '용산구'),
(1, '서명', '도서정가제 강화 및 지역 서점 보호 서명 운동', '교육', '문화 다양성을 지탱하는 지역 서점들이 무너지지 않도록 도서정가제 예외 조항을 삭제하고 지원 체계를 강화해야 합니다.', 'https://save-books.kr/sign', false, 1, '2025-08-10 00:00:00', '2025-09-10 23:59:59', '전라북도', '군산시'),
(1, '청원', '지하철 역사 내 휠체어 리프트 철거 및 엘리베이터 설치 청원', '장애인', '살인 기계나 다름없는 리프트를 모두 철거하고 모든 지하철 역사에 100% 엘리베이터를 설치할 것을 청원합니다.', 'https://barrier-free.kr/petition', false, 1, '2025-12-03 00:00:00', '2026-01-03 23:59:59', '서울특별시', '동대문구'),
(1, '탄원', '특정 지역 혐오 범죄 가해자 엄벌 및 재발 방지 탄원', '인권,범죄/사법', '근거 없는 혐오로 특정 지역민을 비하하고 폭력을 행사한 범죄자에 대해 관용 없는 처벌을 내려주시기를 탄원합니다.', 'https://stop-hate.kr/appeal', false, 1, '2025-07-15 00:00:00', '2025-08-15 23:59:59', '광주광역시', '서구'),
(1, '집회', '대형 플랫폼 수수료 인하 촉구 자영업자 집회', '복지,노동자', '배달 플랫폼의 횡포에 가까운 수수료 인상으로 문을 닫는 가게들이 늘고 있습니다. 공정한 플랫폼 생태계를 요구합니다.', 'https://fair-platform.kr/rally', false, 1, '2025-10-18 15:00:00', '2025-10-18 17:00:00', '서울특별시', '송파구'),
(1, '행사', '제로 웨이스트(Zero Waste) 일상 실천 워크숍', '환경', '쓰레기를 줄이는 구체적인 방법, 고체 치약 만들기, 천연 수세미 사용하기 등 제로 웨이스트 입문자를 위한 실전 강의.', 'https://zero-waste.kr/ws', false, 1, '2025-05-11 14:00:00', '2025-05-11 16:00:00', '부산광역시', '해운대구'),
(1, '서명', '직장 내 괴롭힘 방지법 적용 범위 5인 미만 확대 서명', '노동자,인권', '가장 열악한 환경에서 일하는 노동자일수록 법의 보호가 필요합니다. 모든 일터에 직장 내 괴롭힘 금지법을 적용하십시오.', 'https://stop-work-harassment.kr/sign', false, 1, '2025-03-01 00:00:00', '2025-04-30 23:59:59', '전라남도', '여수시'),
(1, '청원', '농어촌 버스 노선 유지 및 공영제 실시 청원', '복지', '인구 감소로 인한 노선 폐지로 어르신들이 고립되고 있습니다. 버스 공영제를 통해 최소한의 이동권을 보장하십시오.', 'https://rural-bus.kr/petition', false, 1, '2025-09-15 00:00:00', '2025-10-15 23:59:59', '경상북도', '상주시'),
(1, '탄원', '야생동물 보호구역 내 불법 포획 도구 전수조사 및 처벌 탄원', '동물권', '멸종위기종들이 창애와 올가미에 희생되고 있습니다. 대대적인 단속과 가해자들에 대한 강력한 사법 처리를 탄원합니다.', 'https://wildlife-protect.kr/appeal', false, 1, '2025-12-01 00:00:00', '2026-01-31 23:59:59', '전라북도', '무주군'),
(1, '집회', '임대차 3법 사수 및 주거 안정 강화 시민 결의대회', '복지,인권', '세입자들의 최소한의 방어막인 임대차 법안을 무력화하지 마십시오. 주거권은 돈보다 중요한 기본권입니다.', 'https://housing-rights.kr/action', false, 1, '2025-06-12 19:00:00', '2025-06-12 21:00:00', '서울특별시', '마포구'),
(1, '행사', '청소년 노동 인권 교육 강사 양성 과정', '청소년,교육', '학교 현장에서 청소년들에게 정당한 권리를 가르칠 수 있는 전문 강사를 양성합니다. 인권 감수성을 갖춘 분들의 참여를 기다립니다.', 'https://labor-edu.kr/apply', false, 1, '2025-08-16 10:00:00', '2025-08-18 17:00:00', '경기도', '수원시'),
(1, '서명', '플라스틱 빨대 및 컵 규제 유예 철회 서명 운동', '환경', '기후위기 대응은 후퇴해서는 안 됩니다. 플라스틱 규제 정상화를 통해 자원 순환 사회로 나아가야 합니다.', 'https://plastic-ban.kr/sign', false, 1, '2025-11-20 00:00:00', '2025-12-20 23:59:59', '서울특별시', '종로구'),
(1, '청원', '정신건강 고위험군 긴급 지원 및 24시간 상담소 확충 청원', '복지,의료', '급증하는 자살 예방과 정신건강 지원을 위해 지역마다 접근성 높은 상담 센터를 확충하고 전담 인력을 배치해주십시오.', 'https://mental-health.kr/petition', false, 1, '2025-04-01 00:00:00', '2025-05-01 23:59:59', '대전광역시', '중구'),
(1, '탄원', '특정 종교 단체의 인권 유린 행위 수사 및 피해자 구제 탄원', '인권,범죄/사법', '종교의 자유를 빌미로 가해지는 폭력과 노동 착취를 더 이상 묵인하지 마십시오. 철저한 수사와 처벌을 탄원합니다.', 'https://religious-justice.kr/appeal', false, 1, '2025-02-15 00:00:00', '2025-03-15 23:59:59', '경기도', '가평군'),
(1, '집회', '공공 요금 인상 중단 및 서민 물가 안정 촉구', '복지', '난방비, 전기세, 교통비까지 안 오르는 게 없습니다. 고물가 시대, 서민들의 숨통을 조이는 공공요금 인상을 멈추십시오.', 'https://stop-inflation.kr/march', false, 1, '2025-01-10 14:00:00', '2025-01-10 16:00:00', '서울특별시', '중구'),
(1, '행사', '사회적 경제 기업 박람회: 가치를 사는 사람들', '복지', '취약계층 고용, 환경 보호 등 사회적 가치를 실천하는 기업들의 제품을 만나보고 응원하는 행사입니다.', 'https://social-economy.kr/fair', false, 1, '2025-07-05 10:00:00', '2025-07-05 18:00:00', '전라남도', '광주광역시'),
(1, '서명', '지역 신문 지원 특별법 상시법 전환 서명', '인권,범죄/사법', '지역의 목소리를 대변하는 풀뿌리 언론이 사라지지 않도록 지원 체계를 안정화하고 상시법으로 전환해야 합니다.', 'https://local-news.kr/sign', false, 1, '2025-03-25 00:00:00', '2025-04-25 23:59:59', '부산광역시', '동구'),
(1, '청원', '학교 비정규직 차별 해소 및 공정임금제 도입 청원', '노동자,교육', '학교 안의 또 다른 선생님인 급식, 돌봄 노동자들에 대한 차별적 처우를 개선하고 단일 임금 체계를 마련해주십시오.', 'https://school-labor.kr/petition', false, 1, '2025-10-15 00:00:00', '2025-11-15 23:59:59', '인천광역시', '연수구'),
(1, '탄원', '환경 영향 평가 조작 업체 엄벌 및 재조사 탄원', '환경,범죄/사법', '거짓 보고서로 환경 파괴 면죄부를 준 업체와 관계자들을 엄중히 처벌하고 해당 사업을 원점에서 재검토하십시오.', 'https://clean-impact.kr/appeal', false, 1, '2025-11-30 00:00:00', '2025-12-30 23:59:59', '제주특별자치도', '제주시'),
(1, '집회', '포괄임금제 폐지 및 공짜 야근 근절 결의대회', '노동자', '일한 만큼 받는 것은 상식입니다. 야근 수당을 떼어먹는 악습인 포괄임금제를 전면 폐지하고 정당한 대가를 보장받읍시다.', 'https://stop-unpaid-work.kr/rally', false, 1, '2025-05-22 18:30:00', '2025-05-22 20:30:00', '경기도', '성남시'),
(1, '행사', '아빠들의 육아 토크 콘서트: 우리 아이와 친해지는 법', '여성,인권', '육아는 돕는 것이 아니라 함께하는 것입니다. 아빠들이 겪는 육아 고민을 나누고 평등한 가사 문화를 확산하는 행사입니다.', 'https://daddy-care.kr/concert', false, 1, '2025-09-21 14:00:00', '2025-09-21 16:00:00', '서울특별시', '송파구'),
(1, '서명', '교권 보호 및 정당한 교육 활동 보장 서명 운동', '교육,인권', '교사가 학생을 가르치는 일에만 전념할 수 있도록 악성 민원 방지 대책과 교사의 법적 보호 장치를 마련해야 합니다.', 'https://save-teacher.kr/sign', false, 1, '2025-08-25 00:00:00', '2025-09-25 23:59:59', '충청남도', '아산시'),
(1, '청원', '공공 도서관 야간 개방 시간 연장 및 인력 충원 청원', '복지,교육', '직장인과 학생들이 저녁 시간에도 지식의 공간을 누릴 수 있도록 도서관 운영 시간을 연장하고 담당 인력을 정규직으로 배치하십시오.', 'https://library-open.kr/petition', false, 1, '2025-02-05 00:00:00', '2025-03-05 23:59:59', '강원특별자치도', '춘천시'),
(1, '탄원', '유명무실한 청소년 심야 알바 제한 규정 강화 탄원', '청소년,노동자', '보호받아야 할 청소년들이 위험한 배달이나 심야 노동에 노출되고 있습니다. 법 집행을 강화하고 업주들을 엄벌하십시오.', 'https://youth-night.kr/appeal', false, 1, '2025-12-15 00:00:00', '2026-01-15 23:59:59', '대구광역시', '중구');